LiquidPart.Liquid:
{% assign cards = Queries.Contents | query: type: "Card", limit: 3 %}
<div class="row">
{% for card in cards %}
{{ card | shape_build_display: "Summary" | shape_render }}
{% endfor %}
</div>

Portfolio__ListPart.Content:
{% for item in Model.ContentItems %}
<div class="row">
{% assign project = item.Content[item.ContentType] %}
  <div class="col-md-7">
    <a href="#">
      <img class="img-fluid rounded mb-3 mb-md-0" src="{{ project.Picture.Paths.first | asset_url | resize_url: width:700 }}" alt="">
    </a>
  </div>
  <div class="col-md-5">
    <h3>{{ item | display_text }}</h3>
    <p>{{ project.Description.Text }}</p>
    <a class="btn btn-primary" href="{{ item.Content.AutoroutePart.Path }}">View Project
      <span class="glyphicon glyphicon-chevron-right"></span>
    </a>
  </div>
</div>
<hr>
{% endfor %}

{% assign previousText = " Newer Posts" | t %}
{% assign nextText = "Older Posts " | t %}
{% assign previousClass = "previous" | t %}
{% assign nextClass = "next" | t %}
{% assign itemClasses = "page-item" | split: " " %}

{% shape_add_classes Model.Pager "pagination" %}
{% shape_pager Model.Pager previous_text: previousText, next_text: nextText, previous_class: previousClass, next_class: nextClass, item_classes: itemClasses %}

{{ Model.Pager | shape_render }}

