Content__BlogPost.Content:
{% zone "AsideFirst" %}
<h1>{{ Model.ContentItem | display_text }}</h1>
<ol class="breadcrumb">
  <li class="breadcrumb-item">
    <a href="/">Home</a>
  </li>
  <li class="breadcrumb-item active">{{ Model.ContentItem | display_text }}</li>
</ol>
{% assign content = Model.ContentItem.Content[Model.ContentItem.ContentType] %}
<div class="col-lg-12">
  <img class="img-fluid rounded" src="{{ content.Picture.Paths.first | asset_url | resize_url: width:900 }}" alt="">
  <hr>
  {% assign format = "dd MMMM yyyy HH:mm" | t %}
  {% assign dateTime = "DateTime" | shape_new: utc: Model.ContentItem.CreatedUtc, format: format | shape_stringify %}
  <p>Posted on {{ "{0}" | t: dateTime | raw }}</p>
  <hr>       
{{ Model.Content.HtmlBodyPart | shape_render }}
{{ Model.Content.DisqusPart | shape_render }}
</div>
{% endzone %}
